definition.script:
pipeline {
    agent any

    environment {
        RELEASE_VERSION = "1.0.0.${BUILD_NUMBER}"
    }

    stages {
        stage("Checkout"){
            steps {
                cleanWs()
                git credentialsId: 'GITHUB',
                    url: 'https://github.com/wilyoctopus/CICD',
                    branch: 'master'
            }
        }

        stage('Build and test') {
            steps {
                script {
                    try {
                        bat "docker build -t cicd:${RELEASE_VERSION} . --build-arg VER=${RELEASE_VERSION}"
                    }
                    finally {
                        bat 'dotnet test --logger xunit --collect "XPlat Code Coverage" || ver>nul'
                        xunit([xUnitDotNet('**/TestResults/*.xml')])
                        cobertura autoUpdateHealth: false, autoUpdateStability: false, coberturaReportFile: '**/*.cobertura.xml', conditionalCoverageTargets: '70, 0, 0', failUnhealthy: false, failUnstable: false, lineCoverageTargets: '80, 0, 0', maxNumberOfBuilds: 0, methodCoverageTargets: '80, 0, 0', onlyStable: false, sourceEncoding: 'ASCII', zoomCoverageChart: false
                    }
                }
            }
        }

        stage('Publish')
        {
            steps {
                bat "docker tag cicd:${RELEASE_VERSION} wilyoctopus/workshop:${RELEASE_VERSION}"
                bat "docker push wilyoctopus/workshop:${RELEASE_VERSION}"   
            }
        }

        stage('Promote to DEV')
        {
            steps {
                input('Do you want to proceed?')
                bat 'docker stop cicd.dev || ver>nul'
                bat "docker run --rm -d -p 5000:80 --name cicd.dev cicd:${RELEASE_VERSION}"
            }   
        }
    }
}
